import { Injectable } from "@angular/core";

@Injectable
export class UsersService {
    users = [
        {
            name: 'Manu',
            status: 'active'
        },
        {
            name: 'Chris',
            status: 'active'
        },
        {
            name: 'Max',
            status: 'inactive'
        },
        {
            name: 'Anna',
            status: 'inactive'
        }
    ];

    updateStatus(id: number, newStatus: string) {
        this.users[id].status = newStatus;
    }
}
