import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthService } from '../auth.service';
import { ServersService } from '../servers/servers.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  private servers: { id: number, name: string, status: string }[] = [];

  constructor(private serverService: ServersService, private router: Router, private route: ActivatedRoute, private authSer: AuthService) { }

  ngOnInit() {
    this.servers = this.serverService.getServers();
  }

  loadServer(id: number) {
    //this.router.navigate(['servers'], { relativeTo: this.route });
    this.router.navigate(['/servers', id, 'edit'], { queryParams: { allowEdit: '1' }, fragment: 'Loading' });
  }

  OnLogin() {
    this.authSer.login()
  }

  OnLogOut() {

  }

}
<h4>Welcome to Server Manager 4.0</h4>
<p>Manage your Servers and Users.</p>
<button class="btn btn-primary" (click)="loadServer(1)">Load Servers</button>
<hr>
<button class="btn btn-success" (click)="OnLogin()">Login</button>
<button class="btn btn-danger" (click)="OnLogout()">Logout</button>
